ock(void);						// unlock texture		void UnlockCompletely(void);			// unlock texture completely MERGE-CLEANUP		int SetTextureChunkPosition(long);		long GetTextureChunkPosition(void);		int SetTextureFileLoc(struct VfsLocation*);	// set texture file info		int SetTextureVFile(struct V_File *);	// set texture file info	};#endif //_TEXTURE_