#include "VTypes.h"#include "VError.h"#include "VgerFile.h"#include "AppFiles.h"#include "AppText.h"voidVoyagerFile::cVoyagerFile()	{	cFile(LOAD,appFileTypeVoyager,appFileCreator,NULL,TXTGF_VOYAGER);	}intVoyagerFile::FindVoyager()	{	int err;	SetVolume(0);	SetName(TXT_VOYAGER);	err = Find();	if (!err) err = FVerify();	if (err == fileNotFoundErr) { SetName("Virtus Voyager 1.0f1"); err = Find(); if (!err) err = FVerify(); }	if (err == fileNotFoundErr) { SetName("Virtus Voyager 1.0f2"); err = Find(); if (!err) err = FVerify(); }	if (err == fileNotFoundErr) { SetName("Virtus Voyager 1.0f3"); err = Find(); if (!err) err = FVerify(); }	if (err == fileNotFoundErr) { SetName("Virtus Voyager 1.0f4"); err = Find(); if (!err) err = FVerify(); }	return(err);	}